def get_words_from_string(words_in_string: str, word_length: int = 5) -> list[str]:
    words_count: int = round(len(all_words_str) / word_length)
    all_words_list: list = [all_words_str[i * word_length:(i + 1) + word_length] for i in range(words_count)]

    return all_words_list

all_words_str: str = ''
word_length = 5